switch (e.keyCode) {
  case 35: // END
    this.$(v).selectionStart = this.$(v).value.length;
    this.$(v).selectionEnd = this.$(v).value.length;
    break;
  case 36: // HOME
    this.$(v).selectionStart = 0;
    this.$(v).selectionEnd = 0;
    break;
  case 37: // LEFT
    this.$(v).selectionStart -= 1;
    this.$(v).selectionEnd -= 1;
    break;
  case 39: // RIGHT
    this.$(v).selectionEnd += 1;
    this.$(v).selectionStart += 1;
    break;
}

